import React from 'react';
import axios from 'axios';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import Paper from "@material-ui/core/Paper";
import { withStyles, makeStyles } from "@material-ui/core/styles";

const useStyles = theme => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
      width: '18%',
    },
    
  });

//Exports the contents of class 'edit' when called
class Edit extends React.Component {

    //Render returns the <h3> contents 
    constructor() {
        super();

        //This binds contexts to the functions to be run later
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeYear = this.onChangeYear.bind(this);
        this.onChangePoster = this.onChangePoster.bind(this);
        this.handleOpen = this.handleOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);

        //State stores data relevant to the component
        this.state = {
            title: '',
            year: '',
            poster: '',
            open: false,
        }
    }
    //e.target.value is the value property of the DOM element, ie the info submitted in the form
    //This is when the functions are called
    //componentDidMount is the lifecycle hook
    componentDidMount() {
        axios.get('http://localhost:4000/api/movies')
            .then((response) => {
                    this.setState({ movies: response.data})
                }
            )
    //if there is an error an error message will be returned
            .catch(
                (error)=>{
                        console.log(error)
                }
            );
    }
    componentDidMount(){
        console.log(this.props.match.params.id);

        //Returns a movie by id and updates the state of the movie
        axios.get('http://localhost:4000/api/movies/'+this.props.match.params.id)
        .then(response =>{
            this.setState({
                _id:response.data._id,
                title:response.data.title,
                year:response.data.year,
                poster:response.data.poster
            })
        })
        .catch((error)=>{
            console.log(error);
        });
    }
    onChangeTitle(e) {
        this.setState({
            title: e.target.value
        })
    }
    onChangePoster(e) {
        this.setState({
            poster: e.target.value
        })
    }

    onChangeYear(e) {
        this.setState({
            year: e.target.value
        })
    }
    onSubmit(e) {
        e.preventDefault();

        //alert returns a pop up so we know the data has been entered

    console.log(this.state.title);
    console.log(this.state.year);
    console.log(this.state.poster);

        alert("Movie:" + this.state.title + " " + this.state.year
            + " " + this.state.poster);
            const newMovie = {
                title: this.state.title,
                year: this.state.year,
                poster: this.state.poster,
                _id: this.state._id
            }
         
            //calls findByIdAndUpdate method
            axios.patch('http://localhost:4000/api/movies/'+this.state._id, newMovie)
            .then(res =>{
                console.log(res.data)
            })
            .catch();
            
    }
    handleOpen() {
        this.setState({ open: true });
    };
    
    handleClose(){
        this.setState({ open: false });
    };

    //<form> creates a form for the user to interact with and add movie information
    render() {
        const { classes } = this.props;
        return (
            <div>
                <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={this.state.open}
        onClose={this.handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={this.state.open}>
          <div className={classes.paper}>
          <form onSubmit={this.onSubmit}>
            <h2 id="transition-modal-title">Add a Book</h2>
            <div className="form-group"><label>Book Name: &nbsp;</label>
            <input type='text' className='form-control' value={this.state.title} onChange={this.onChangeTitle}></input></div>
            <br></br>
            <div className="form-group"><label>Book Author: &nbsp;</label>
            <input type='text' className='form-control' value={this.state.year} onChange={this.onChangeYear}></input></div>
            <br></br>
            <div className="form-group"><label>Year: &nbsp;</label>
            <input type='text' className='form-control' value={this.state.poster}  onChange={this.onChangePoster}></input></div>
            <br></br>
            <label for="genres">Genre: &nbsp;</label>
            <select name="genres" id="genres">
            <option value="Fantasy">Fantasy</option>
            <option value="Fiction">Historical Fiction</option>
            <option value="Horror">Horror</option>
            <option value="Biography">Biography</option>
            <option value="Biography">Other</option>
            </select>
            <br></br><br></br>
            <Button type='submit' variant="contained" color="secondary" onClick={this.handleOpen.bind(this)}>
        Add Book
      </Button>
      </form>
          </div>
        </Fade>
      
      </Modal>
            </div>
        );
    }
}
export default withStyles(useStyles, { withTheme: true })(Edit)
