import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import "font-awesome/css/font-awesome.min.css";
import Rating from '@material-ui/lab/Rating';
import Button from '@material-ui/core/Button';
import { Movies } from './movies';
import {Container} from 'reactstrap';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { useTheme } from '@material-ui/core/styles';
import axios from 'axios';


const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white
  },
  body: {
    fontSize: 14
  }
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover
    }
  }
}))(TableRow);

function createBook(title, author, year, genre, rating) {
  return { title, author, year, genre, rating};
}

const rows = [
  createBook( 'The Name Of the Wind', 'Patrick Rothfuss', 2007, 'Fantasy', 4),
  createBook('Nevernight', 'Jay Kristoff', 2016, 'Fantasy', 3),
  createBook('All The Light We Cannot See', 'Anthony Doerr', 2014, 'Historical Fiction', 4),
  createBook('The Troop', 'Nick Cutter', 2014, 'Horror', 3),
  createBook('Jump', 'Daniella Moyles', 2020, 'Biography', 3),
  createBook('The Name Of the Wind', 'Patrick Rothfuss', 2007, 'Fantasy', 4),
  createBook('Nevernight', 'Jay Kristoff', 2016, 'Fantasy', 3),
  createBook('All The Light We Cannot See', 'Anthony Doerr', 2014, 'Historical Fiction', 4),
  createBook('The Troop', 'Nick Cutter', 2014, 'Horror', 3),
  createBook('Jump', 'Daniella Moyles', 2020, 'Biography', 3),
  

];

const styles = theme => ({
  table: {
    minWidth: 700,
    width: "97%",
    float: 'right',
    padding: '50px 20px',
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',

    
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    width: '18%',
  },
  
});

export class MyBooks extends React.Component{
 //state stores data relevent to the component
 state = {

  movies: []
};
constructor(){
  super();
  this.ReloadDataMethod = this.ReloadDataMethod.bind(this);
  }
  
//componentDidMount executes after the first render on the client side
//axios retrives movie information from the link
ReloadDataMethod(){
  axios.get('http://localhost:4000/api/movies')
  .then((response)=>{
  this.setState({movies: response.data.movies})
  })
  .catch((error)=>{
  console.log(error);
  });
  }
  
componentDidMount() {
  axios.get('http://localhost:4000/api/movies')
      .then((response) => {
              this.setState({ movies: response.data})
          }
      )
//if there is an error an error message will be returned
      .catch(
          (error)=>{
                  console.log(error)
          }
      );
}

  render(){
    const { classes } = this.props;
    const { movies } = this.state;
      return(
          <div>
            
          <TableContainer component={Paper}>
          
            <Table aria-label="customized table">
              <TableHead >
                <TableRow>
                  <StyledTableCell style={{ height: "32px", backgroundColor: "#DB3D44"  }}>Book List 2021</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} >Book Author</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} >Year</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} >Genre</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} >Book Rating</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} align="left">&nbsp;&nbsp;&nbsp;&nbsp;</StyledTableCell>
                  <StyledTableCell style={{ backgroundColor: "#DB3D44" }} ></StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
              
                  <StyledTableRow>
           
                    <StyledTableCell  component="th" scope="row">{movies.title}
                    </StyledTableCell>
                    <TableCell >{movies.title}</TableCell>
                    <TableCell ></TableCell>
                    <TableCell ></TableCell>
                    <TableCell > 
            <Rating name="size-medium" defaultValue={2} />
            </TableCell>
                    <TableCell ><i className="fa fa-fw fa-pencil" style={{ fontSize: "1.3em", color: "darkblue" }}/>&nbsp;&nbsp;<i className="fa fa-fw fa-times" style={{ fontSize: "1.3em", color: "red" }} /></TableCell>
                    <TableCell >&nbsp;&nbsp;&nbsp;</TableCell>

                  </StyledTableRow>
                 
              </TableBody>
            </Table>
          </TableContainer>

          <br></br><br></br>

           
          </div>
      );
  }
}
