import "semantic-ui-css/semantic.min.css";
export const NEWS_API_KEY = "005a4a7b510a42c6bb35f4f4cf3d63a3";


export const getBitcoinArticles = async () => {
    const response = await fetch(
      `https://newsapi.org/v2/top-headlines?q=trump&&pageSize=4&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`
    );
    const json = await response.json();
    return json;
  };
  
  export const getArticles = async topic => {
    const response = await fetch(
      `https://newsapi.org/v2/everything?q=${topic}&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`
    );
    const json = await response.json();
    return json;
  };
