import React from "react";
import { Grid, Header, Image, List } from "semantic-ui-react";


const ArticleItem = (props) => {
    const { movies } = props;
    return (
      <List.Item style={{ padding: 30 }}>
        <Grid>
          <Grid.Column
            width={11}
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "flex-start",
              textAlign: "left",
            }}
          >
            <Header as="h3">{movies.title}</Header>
            <List.Description style={{ margin: "20px 0" }}>
              {movies.title}
            </List.Description>
            <List bulleted horizontal>
              <List.Item>
                <a href={movies.title}>{movies.title}</a>
              </List.Item>

            </List>
          </Grid.Column>

        </Grid>
      </List.Item>
    );
  };
  
  const ArticleList = (props) => {
    return (
      <List divided style={{ maxWidth: 900, margin: "0 auto" }}>
        {props.movies.map((movies, index) => (
          <ArticleItem movies={movies} key={movies.title + index} />
        ))}
      </List>
    );
  };

export default ArticleList;
