import React, { useState } from "react";  
import axios from 'axios';
import getBooksArticles from '../components/getBooksArticles';
import ArticleList from '../components/getArticlesList';
import getArticles from '../components/getArticlesList';
import SearchBar from "./components/searchBar";
import "semantic-ui-css/semantic.min.css";
import { Grid, Header, Image, List, Container } from "semantic-ui-react";
export const NEWS_API_KEY = "005a4a7b510a42c6bb35f4f4cf3d63a3";


class News extends React.Component {
    state = {
      articles: [],
      searchTopic: "",
      totalResults: "",
      loading: false,
      apiError: ""
    };
  
    searchForTopic = async topic => {
      try {
        this.setState({ loading: true });
        const response = await getArticles(topic);
        this.setState({
          articles: response.articles,
          searchTopic: topic,
          totalResults: response.totalResults
        });
      } catch (error) {
        this.setState({ apiError: "Could not find any articles" });
      }
      this.setState({ loading: false });
    };
  
    render() {
      const {
        articles,
        apiError,
        loading,
        searchTopic,
        totalResults
      } = this.state;
      return (
        <Container>
          <Header as="h2" style={{ textAlign: "center", margin: 20 }}>
            Search for a topic
          </Header>
          <SearchBar searchForTopic={this.searchForTopic} />
          <p style={{ textAlign: "center" }}>
            Powered by <a href="https://newsapi.org/">NewsAPI.org</a>
          </p>
          {loading && (
            <p style={{ textAlign: "center" }}>Searching for articles...</p>
          )}
          {articles.length > 0 && (
            <Header as="h4" style={{ textAlign: "center", margin: 20 }}>
              Found {totalResults} articles on "{searchTopic}"
            </Header>
          )}
          {articles.length > 0 && <ArticleList articles={articles} />}
          {apiError && <p>Could not fetch any articles. Please try again.</p>}
        </Container>
      );
    }
  }
  
  export default News;
